// --- MANEJO DE VISTAS (Lógica de la Aplicación) ---

// Ejecutar al cargar la página
document.addEventListener('DOMContentLoaded', function() {
    // 1. Configuración de Event Listeners
    document.getElementById('login-form').addEventListener('submit', handleLogin);
    document.getElementById('logout-button').addEventListener('click', handleLogout);
    document.getElementById('add-equipment-button').addEventListener('click', openAddModal);
    document.getElementById('crud-form').addEventListener('submit', handleSaveEquipment);
    
    // Funcionalidad de Desplegar/Colapsar
    document.getElementById('toggle-uma').addEventListener('click', () => toggleSection('uma-container', 'toggle-uma'));
    document.getElementById('toggle-fc').addEventListener('click', () => toggleSection('fc-container', 'toggle-fc'));
    
    // Guardar Observaciones
    document.getElementById('obs-form').addEventListener('submit', handleSaveObservation);
    
    // Cierre de Modales
    document.querySelectorAll('.close-button').forEach(button => {
        button.addEventListener('click', function() {
            const modalId = this.dataset.modal;
            document.getElementById(modalId).style.display = 'none';
        });
    });
    window.addEventListener('click', handleModalOutsideClick);
});

// FUNCIÓN: Desplegar/Colapsar sección
function toggleSection(containerId, buttonId) {
    const container = document.getElementById(containerId);
    const button = document.getElementById(buttonId);

    if (container.classList.contains('hidden')) {
        container.classList.remove('hidden');
        button.textContent = 'Colapsar [-]';
    } else {
        container.classList.add('hidden');
        button.textContent = 'Ampliar [+]';
    }
}

// FUNCIÓN: Manejo de Login y Logout (Se mantiene igual)
function handleLogin(e) {
    e.preventDefault();
    const username = document.getElementById('username').value;
    const password = document.getElementById('password').value;
    const message = document.getElementById('login-message');

    if (username === 'admin' && password === '12345') {
        message.textContent = '';
        document.getElementById('login-container').classList.add('hidden');
        document.getElementById('main-content').classList.remove('hidden');
        renderAllTables(EQUIPMENT_DATA); // Renderiza ambas tablas al ingresar
        
        // Colapsar la sección FC por defecto si se desea (opcional)
        // toggleSection('fc-container', 'toggle-fc');
        
    } else {
        message.textContent = 'Usuario o contraseña incorrectos.';
    }
}

function handleLogout() {
    document.getElementById('main-content').classList.add('hidden');
    document.getElementById('login-container').classList.remove('hidden');
}

// FUNCIÓN: Renderizar ambas tablas (Se mantiene igual)
function renderAllTables(data) {
    const umas = data.filter(eq => eq.tipo === 'MANEJADORA');
    const fancoils = data.filter(eq => eq.tipo === 'FAN & COIL');

    renderEquipmentTable(umas, '#uma-table tbody');
    renderEquipmentTable(fancoils, '#fc-table tbody');
}


// OPERACIÓN R (READ) - Generación de la Tabla (Se mantiene igual)
function renderEquipmentTable(data, tbodySelector) {
    const tbody = document.querySelector(tbodySelector);
    tbody.innerHTML = '';

    data.forEach(equipment => {
        const row = tbody.insertRow();
        
        let statusClass = '';
        if (equipment.estado === 'Activo') statusClass = 'active-status';
        else if (equipment.estado === 'Inactivo') statusClass = 'inactive-status';
        else if (equipment.estado === 'Mantenimiento') statusClass = 'maintenance-status';
        else statusClass = '';

        row.insertCell().textContent = equipment.id; 
        row.insertCell().textContent = equipment.seccion; 
        row.insertCell().textContent = equipment.tipo; 
        row.insertCell().textContent = equipment.marca; 
        row.insertCell().textContent = equipment.tamaño; 
        row.insertCell().textContent = equipment.modelo; 
        row.insertCell().textContent = equipment.serie; 
        
        const statusCell = row.insertCell();
        statusCell.textContent = equipment.estado;
        statusCell.classList.add(statusClass);

        row.insertCell().textContent = equipment.ultimo_mantenimiento || 'N/A';

        const actionCell = row.insertCell();

        const detailButton = createButton('Detalles', 'action-button', showEquipmentDetails, equipment.id);
        const editButton = createButton('Editar', 'action-button', openEditModal, equipment.id);
        const deleteButton = createButton('Eliminar', 'action-button-danger', confirmDelete, equipment.id);

        actionCell.appendChild(detailButton);
        actionCell.appendChild(editButton);
        actionCell.appendChild(deleteButton);
    });
}

// Función auxiliar para crear botones (Se mantiene igual)
function createButton(text, className, handler, id) {
    const button = document.createElement('button');
    button.textContent = text;
    button.classList.add(className);
    button.dataset.equipmentId = id;
    button.addEventListener('click', handler);
    return button;
}

// OPERACIÓN R (READ) - Mostrar detalles en modal (Modificado para edición de Obs.)
function showEquipmentDetails(event) {
    const equipmentId = event.target.dataset.equipmentId;
    const equipment = EQUIPMENT_DATA.find(eq => eq.id === equipmentId);

    if (equipment) {
        document.getElementById('detail-title').textContent = `Detalles del Equipo: ${equipment.nombre} (${equipment.id})`;
        const detailsBody = document.getElementById('detail-body');
        
        // Asignar ID y texto de Observaciones al formulario de edición
        document.getElementById('obs-equipment-id').value = equipment.id;
        document.getElementById('obs-textarea').value = equipment.detalles_especificos.observaciones || '';
        
        // Detalle de la tabla (sin observaciones aquí)
        let html = '<h4>Información General</h4><ul>';
        html += `<li><strong>ID Equipo:</strong> ${equipment.id}</li>`;
        html += `<li><strong>Sección/Área:</strong> ${equipment.seccion}</li>`;
        html += `<li><strong>Tipo:</strong> ${equipment.tipo}</li>`;
        html += `<li><strong>Marca:</strong> ${equipment.marca}</li>`;
        html += `<li><strong>Capacidad:</strong> ${equipment.tamaño}</li>`;
        html += `<li><strong>Modelo:</strong> ${equipment.modelo}</li>`;
        html += `<li><strong>No. Serie:</strong> ${equipment.serie}</li>`;
        html += `<li><strong>Estado de Actividad:</strong> <span class="${equipment.estado === 'Activo' ? 'active-status' : equipment.estado === 'Inactivo' ? 'inactive-status' : 'maintenance-status'}">${equipment.estado}</span></li>`;
        html += `<li><strong>Último Mantenimiento:</strong> ${equipment.ultimo_mantenimiento || 'N/A'}</li>`;
        html += '</ul>';

        // Detalle específico (otros campos técnicos)
        html += '<h4>Detalles Técnicos Adicionales</h4><ul>';
        for (const key in equipment.detalles_especificos) {
             if (key === 'observaciones') continue; 
            const formattedKey = key.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
            html += `<li><strong>${formattedKey}:</strong> ${equipment.detalles_especificos[key]}</li>`;
        }
        html += '</ul>';
        
        detailsBody.innerHTML = html;
        document.getElementById('detail-modal').style.display = 'block';
    }
}

// NUEVA FUNCIÓN: Guardar Observación (Actualización específica)
function handleSaveObservation(e) {
    e.preventDefault();
    const equipmentId = document.getElementById('obs-equipment-id').value;
    const newObservation = document.getElementById('obs-textarea').value;
    
    const equipment = EQUIPMENT_DATA.find(eq => eq.id === equipmentId);
    
    if (equipment) {
        // Asegurar que el objeto detalles_especificos existe
        if (!equipment.detalles_especificos) {
            equipment.detalles_especificos = {};
        }
        equipment.detalles_especificos.observaciones = newObservation;
        alert(`Observación del equipo ${equipmentId} guardada con éxito.`);
        document.getElementById('detail-modal').style.display = 'none';
    } else {
        alert('Error: Equipo no encontrado.');
    }
}


// OPERACIONES C, U, D (Se mantienen iguales)

function openAddModal() {
    const form = document.getElementById('crud-form');
    form.reset();
    document.getElementById('crud-title').textContent = '➕ Agregar Nuevo Equipo';
    document.getElementById('crud-submit-button').textContent = 'Crear Equipo';
    document.getElementById('crud-equipment-id').value = '';
    
    document.getElementById('crud-id').disabled = false;
    document.getElementById('crud-tipo').disabled = false; 

    document.getElementById('crud-id').value = `NUEVO-FC-${Math.floor(Math.random() * 1000)}`;
    document.getElementById('crud-tipo').value = 'FAN & COIL'; 
    document.getElementById('crud-mantenimiento').value = new Date().toISOString().substring(0, 10);
    
    document.getElementById('crud-modal').style.display = 'block';
}

function openEditModal(event) {
    const equipmentId = event.target.dataset.equipmentId;
    const equipment = EQUIPMENT_DATA.find(eq => eq.id === equipmentId);
    
    if (equipment) {
        document.getElementById('crud-title').textContent = `✏️ Editar Equipo: ${equipment.id}`;
        document.getElementById('crud-submit-button').textContent = 'Guardar Cambios';
        
        document.getElementById('crud-equipment-id').value = equipment.id;
        document.getElementById('crud-id').value = equipment.id;
        document.getElementById('crud-id').disabled = true; 
        
        document.getElementById('crud-tipo').value = equipment.tipo;
        document.getElementById('crud-tipo').disabled = true; 
        
        document.getElementById('crud-seccion').value = equipment.seccion;
        document.getElementById('crud-capacidad').value = equipment.tamaño;
        document.getElementById('crud-modelo').value = equipment.modelo;
        document.getElementById('crud-serie').value = equipment.serie;
        document.getElementById('crud-estado').value = equipment.estado;
        document.getElementById('crud-mantenimiento').value = equipment.ultimo_mantenimiento;
        
        document.getElementById('crud-modal').style.display = 'block';
    }
}

function handleSaveEquipment(e) {
    e.preventDefault();
    
    const originalId = document.getElementById('crud-equipment-id').value;
    const newId = document.getElementById('crud-id').value;
    const tipo = document.getElementById('crud-tipo').value;
    
    // Recuperar la observación existente antes de crear/actualizar el objeto
    let existingObservation = '';
    if (originalId) {
        const existingEquipment = EQUIPMENT_DATA.find(eq => eq.id === originalId);
        if (existingEquipment && existingEquipment.detalles_especificos) {
            existingObservation = existingEquipment.detalles_especificos.observaciones || '';
        }
    }

    const newEquipment = {
        id: newId,
        nombre: newId,
        tipo: tipo,
        marca: 'TRANE', // Marca fija para ambos tipos según las correcciones
        seccion: document.getElementById('crud-seccion').value,
        tamaño: document.getElementById('crud-capacidad').value,
        modelo: document.getElementById('crud-modelo').value,
        serie: document.getElementById('crud-serie').value,
        estado: document.getElementById('crud-estado').value,
        ultimo_mantenimiento: document.getElementById('crud-mantenimiento').value,
        detalles_especificos: { 
            fabricante: 'TRANE', 
            voltaje: 'NA', 
            fase: 'NA', 
            refrigerante: 'NA',
            observaciones: existingObservation 
        }
    };
    
    if (originalId) {
        const index = EQUIPMENT_DATA.findIndex(eq => eq.id === originalId);
        if (index !== -1) {
            EQUIPMENT_DATA[index] = newEquipment;
            alert(`Equipo ${originalId} actualizado con éxito.`);
        }
    } else {
        if (EQUIPMENT_DATA.some(eq => eq.id === newId)) {
            alert(`ERROR: Ya existe un equipo con el ID ${newId}. Por favor, use otro ID.`);
            return;
        }
        EQUIPMENT_DATA.push(newEquipment);
        alert(`Equipo ${newId} creado con éxito.`);
    }

    document.getElementById('crud-modal').style.display = 'none';
    renderAllTables(EQUIPMENT_DATA);
}

function confirmDelete(event) {
    const equipmentId = event.target.dataset.equipmentId;
    if (confirm(`¿Estás seguro de que deseas eliminar el equipo con ID: ${equipmentId}? Esta acción es irreversible.`)) {
        deleteEquipment(equipmentId);
    }
}

function deleteEquipment(equipmentId) {
    const initialLength = EQUIPMENT_DATA.length;
    EQUIPMENT_DATA = EQUIPMENT_DATA.filter(eq => eq.id !== equipmentId);

    if (EQUIPMENT_DATA.length < initialLength) {
        alert(`Equipo ${equipmentId} eliminado con éxito.`);
        renderAllTables(EQUIPMENT_DATA);
    } else {
        alert(`ERROR: No se encontró el equipo con ID ${equipmentId}.`);
    }
}

function handleModalOutsideClick(event) {
    const detailModal = document.getElementById('detail-modal');
    const crudModal = document.getElementById('crud-modal');
    if (event.target == detailModal) {
        detailModal.style.display = 'none';
    }
    if (event.target == crudModal) {
        crudModal.style.display = 'none';
    }
}